import{_ as e,c as t,o as l,a as r}from"./app.a59c9aa2.js";const T=JSON.parse('{"title":"MQTT \u57FA\u7840\u901F\u901A","titleTemplate":"Quick Start","description":"","frontmatter":{"title":"MQTT \u57FA\u7840\u901F\u901A","titleTemplate":"Quick Start"},"headers":[{"level":2,"title":"\u6982\u89C8","slug":"\u6982\u89C8","link":"#\u6982\u89C8","children":[]},{"level":2,"title":"\u53D1\u5E03/\u8BA2\u9605\u6A21\u5F0F","slug":"\u53D1\u5E03-\u8BA2\u9605\u6A21\u5F0F","link":"#\u53D1\u5E03-\u8BA2\u9605\u6A21\u5F0F","children":[]},{"level":2,"title":"\u62A5\u6587","slug":"\u62A5\u6587","link":"#\u62A5\u6587","children":[{"level":3,"title":"QoS","slug":"qos","link":"#qos","children":[]}]},{"level":2,"title":"\u4F1A\u8BDD\u4FDD\u6301","slug":"\u4F1A\u8BDD\u4FDD\u6301","link":"#\u4F1A\u8BDD\u4FDD\u6301","children":[]},{"level":2,"title":"\u9057\u5631\u673A\u5236","slug":"\u9057\u5631\u673A\u5236","link":"#\u9057\u5631\u673A\u5236","children":[]},{"level":2,"title":"\u53C2\u8003","slug":"\u53C2\u8003","link":"#\u53C2\u8003","children":[]}],"relativePath":"notes/quick-start/mqtt-protocol.md","lastUpdated":1661928657000}'),o={name:"notes/quick-start/mqtt-protocol.md"},a=r('<h1 id="mqtt-\u57FA\u7840\u901F\u901A" tabindex="-1">MQTT \u57FA\u7840\u901F\u901A <a class="header-anchor" href="#mqtt-\u57FA\u7840\u901F\u901A" aria-hidden="true">#</a></h1><h2 id="\u6982\u89C8" tabindex="-1">\u6982\u89C8 <a class="header-anchor" href="#\u6982\u89C8" aria-hidden="true">#</a></h2><p>\u6240\u8C13 MQTT \u5373 Message Queuing Telemetry Transport\uFF0C\u8BD1\u4E3A <code>\u6D88\u606F\u961F\u5217\u9065\u6D4B\u4F20\u8F93\u534F\u8BAE</code>\u3002</p><p>\u5176\u7279\u70B9\u5982\u4E0B\uFF1A</p><ul><li>\u662F\u4E00\u79CD\u57FA\u4E8E <code>\u53D1\u5E03/\u8BA2\u9605\uFF08publish/subscribe\uFF09</code> \u6A21\u5F0F\u7684&quot;\u8F7B\u91CF\u7EA7&quot;\u901A\u8BAF\u534F\u8BAE</li><li>\u57FA\u4E8E TCP/IP \u534F\u8BAE</li><li>\u652F\u6301 QoS\uFF08Quality of Service\uFF0C\u6307\u4E00\u4E2A\u7F51\u7EDC\u80FD\u591F\u5229\u7528\u5404\u79CD\u57FA\u7840\u6280\u672F\uFF0C\u4E3A\u6307\u5B9A\u7684\u7F51\u7EDC\u901A\u4FE1\u63D0\u4F9B\u66F4\u597D\u7684\u670D\u52A1\u80FD\u529B\uFF0C\u662F\u7F51\u7EDC\u7684\u4E00\u79CD\u5B89\u5168\u673A\u5236\uFF0C \u662F\u7528\u6765\u89E3\u51B3\u7F51\u7EDC\u5EF6\u8FDF\u548C\u963B\u585E\u7B49\u95EE\u9898\u7684\u4E00\u79CD\u6280\u672F\uFF08\u767E\u5EA6\u767E\u79D1\uFF09\uFF09</li><li>\u62A5\u6587\u5C0F\uFF0C\u5F00\u9500\u4F4E\uFF0C\u5E26\u5BBD\u5360\u7528\u4F4E</li></ul><h2 id="\u53D1\u5E03-\u8BA2\u9605\u6A21\u5F0F" tabindex="-1">\u53D1\u5E03/\u8BA2\u9605\u6A21\u5F0F <a class="header-anchor" href="#\u53D1\u5E03-\u8BA2\u9605\u6A21\u5F0F" aria-hidden="true">#</a></h2><p>MQTT \u4E2D\uFF0Cbroker \u8D1F\u8D23\u4F9D\u636E topic \u4E2D\u8F6C publisher \u53D1\u5E03\u7684\u4FE1\u606F\u7ED9 subscriber\u3002MQTT \u4E2D\u7684 topic \u4E0D\u9700\u8981\u9884\u5148\u5EFA\u7ACB\uFF0C\u53D1\u5E03\u8005\u53D1\u9001\u6D88\u606F\u5230\u67D0\u4E2A\u4E3B\u9898\u3001\u8BA2\u9605\u8005\u8BA2\u9605\u67D0\u4E2A\u4E3B\u9898\u7684\u65F6\u5019\uFF0Cbroker \u5C31\u4F1A\u81EA\u52A8\u521B\u5EFA\u8FD9\u4E2A\u4E3B\u9898\u3002\u53E6\u5916\uFF0Ctopic \u662F\u6709\u5C42\u7EA7\u7684\uFF0C\u5C42\u7EA7\u652F\u6301\u901A\u914D\u7B26\uFF1A<code>+ </code>\u4E0E <code>#</code>\uFF1A</p><ul><li><code>+</code> \u901A\u914D\u5355\u5C42\uFF0C\u6BD4\u5982 <code>news/+</code> \u53EF\u4EE5\u5339\u914D <code>news/sports</code>\uFF0C<code>news/+/basketball</code> \u53EF\u5339\u914D\u5230 <code>news/sports/basketball</code></li><li><code>#</code> \u901A\u914D\u591A\u5C42\uFF0C\u6BD4\u5982 <code>news/#</code> \u53EF\u4EE5\u5339\u914D <code>news</code>\u3001 <code>news/sports</code>\u3001<code>news/sports/basketball</code> \u4EE5\u53CA <code>news/sports/basketball/x</code> \u7B49\u7B49</li></ul><h2 id="\u62A5\u6587" tabindex="-1">\u62A5\u6587 <a class="header-anchor" href="#\u62A5\u6587" aria-hidden="true">#</a></h2><p>MQTT \u7684\u6D88\u606F\u4E3B\u8981\u7531\u4E09\u90E8\u5206\u7EC4\u6210\uFF1A\u56FA\u5B9A\u957F\u5EA6\u5934\u90E8\u3001\u53EF\u53D8\u957F\u5EA6\u5934\u90E8\u3001payload\u3002</p><table><thead><tr><th>\u7EC4\u6210\u90E8\u5206</th><th>\u8BF4\u660E</th></tr></thead><tbody><tr><td>\u56FA\u5B9A\u957F\u5EA6\u5934\u90E8</td><td>\u5360 2 \u5B57\u8282\u5927\u5C0F\uFF0C\u6240\u6709\u6D88\u606F\u4E2D\u5747\u5B58\u5728</td></tr><tr><td>\u53EF\u53D8\u957F\u5EA6\u5934\u90E8</td><td>\u67D0\u4E9B\u7C7B\u578B\u7684\u6D88\u606F\u4E2D\u5B58\u5728</td></tr><tr><td>payload</td><td>\u67D0\u4E9B\u7C7B\u578B\u7684\u6D88\u606F\u4E2D\u5B58\u5728</td></tr></tbody></table><p>MQTT \u7684\u4E3B\u8981\u6D88\u606F\u7C7B\u578B\u6709\uFF1A</p><ul><li>CONNECT / CONNACK - connect</li><li>PUBLISH / PUBACK - publish</li><li>SUBSCRIBE / SUBACK - subscribe</li><li>UNSUBSCRIBE / UNSUBACK - unsubscribe</li><li>PINGREQ / PINGRESP - ping\uFF08\u5FC3\u8DF3\uFF09</li><li>DISCONNECT - disconnect</li></ul><p>\u7279\u6B8A\u5730\uFF0CPINGREQ / PINGRESP \u548C DISCONNECT \u4E24\u79CD\u6D88\u606F\u6CA1\u6709\u53EF\u53D8\u5934\u90E8\u3001payload\uFF0C\u5176\u5927\u5C0F\u53EA\u6709 2 \u5B57\u8282\u3002\u53E6\u5916\uFF0C\u5728 CONNECT \u62A5\u6587\u7684\u53EF\u53D8\u957F\u5EA6\u5934\u90E8\u91CC\uFF0C\u6709\u4E2A Protocol Version \u7684\u5B57\u6BB5\u3002\u4E3A\u4E86\u8282\u7701\u7A7A\u95F4\uFF0C\u53EA\u6709\u4E00\u4E2A\u5B57\u8282\u3002\u6240\u4EE5\u7248\u672C\u53F7\u4E0D\u662F\u6309\u7167\u5B57\u7B26\u4E32 &quot;3.1.1&quot; \u5B58\u653E\u7684\uFF0C\u800C\u662F\u4F7F\u7528\u6570\u5B57 4 \u6765\u8868\u793A 3.1.1 \u7248\u672C\u3002</p><h3 id="qos" tabindex="-1">QoS <a class="header-anchor" href="#qos" aria-hidden="true">#</a></h3><p>\u9488\u5BF9\u4E0D\u540C\u7684\u573A\u666F\uFF0CMQTT \u8BBE\u8BA1\u4E86 3 \u4E2A QoS \u7B49\u7EA7\uFF1A</p><ul><li><p>QoS0 \u81F3\u591A\u4E00\u6B21 - \u662F\u4E00\u79CD &quot;fire and forget&quot; \u7684\u6D88\u606F\u53D1\u9001\u6A21\u5F0F\uFF1ASender (\u53EF\u80FD\u662F Publisher \u6216\u8005 Broker) \u53D1\u9001\u4E00\u6761\u6D88\u606F\u4E4B\u540E\uFF0C\u5C31\u4E0D\u518D\u5173\u5FC3\u5B83\u6709\u6CA1\u6709\u53D1\u9001\u5230\u5BF9\u65B9\uFF0C\u4E5F\u4E0D\u8BBE\u7F6E\u4EFB\u4F55\u91CD\u53D1\u673A\u5236\u3002\u9002\u7528\u4E8E\u73AF\u5883\u4F20\u611F\u5668\u6570\u636E\u4F20\u8F93\u7B49\uFF0C\u4E22\u5931\u4E00\u6B21\u8BB0\u5F55\u65E0\u6240\u8C13\uFF0C\u56E0\u4E3A\u5F88\u5FEB\u4F1A\u6709\u65B0\u7684\u6570\u636E\u4E0A\u62A5\u3002</p></li><li><p>QoS1 \u81F3\u5C11\u4E00\u6B21 - \u5305\u542B\u4E86\u7B80\u5355\u7684\u91CD\u53D1\u673A\u5236\uFF0CSender \u53D1\u9001\u6D88\u606F\u4E4B\u540E\u7B49\u5F85\u63A5\u6536\u8005\u7684 ACK\uFF0C\u5982\u679C\u6CA1\u6536\u5230 ACK \u5219\u91CD\u65B0\u53D1\u9001\u6D88\u606F\u3002\u8FD9\u79CD\u6A21\u5F0F\u80FD\u4FDD\u8BC1\u6D88\u606F\u81F3\u5C11\u80FD\u5230\u8FBE\u4E00\u6B21\uFF0C\u4F46\u53EF\u80FD\u4F1A\u56E0\u6B64\u4EA7\u751F\u91CD\u590D\u6D88\u606F\u3002</p></li><li><p>QoS2 \u53EA\u6709\u4E00\u6B21 - \u8BBE\u8BA1\u4E86 <a href="https://docs.emqx.io/broker/v3/en/protocol.html#qos2-message-publish-and-subscribe" target="_blank" rel="noreferrer">\u7565\u5FAE\u590D\u6742</a> \u7684\u91CD\u53D1\u548C\u91CD\u590D\u6D88\u606F\u53D1\u73B0\u673A\u5236\uFF0C\u4FDD\u8BC1\u6D88\u606F\u5230\u8FBE\u5BF9\u65B9\u5E76\u4E14\u4E25\u683C\u503C\u5230\u8FBE\u4E00\u6B21\uFF0C\u9002\u7528\u4E8E\u8BA1\u8D39\u7CFB\u7EDF\u4E2D\u3002</p></li></ul><h2 id="\u4F1A\u8BDD\u4FDD\u6301" tabindex="-1">\u4F1A\u8BDD\u4FDD\u6301 <a class="header-anchor" href="#\u4F1A\u8BDD\u4FDD\u6301" aria-hidden="true">#</a></h2><p>MQTT \u534F\u8BAE\u901A\u8FC7\u5728 connect \u62A5\u6587\u91CC\uFF0C\u914D\u7F6E <code>keepalive</code> \u5B57\u6BB5\uFF0C\u6765\u8BBE\u7F6E\u5FC3\u8DF3\u5305\uFF08PINGREQ/PINGRESP\uFF09\u7684\u53D1\u9001\u65F6\u95F4\u95F4\u9694\u3002\u5F53\u957F\u65F6\u95F4\u65E0\u6CD5\u6536\u5230\u8BBE\u5907\u7684 PINGREQ \u7684\u65F6\u5019\uFF0Cbroker \u5C31\u4F1A\u8BA4\u4E3A\u8BBE\u5907\u5DF2\u7ECF\u4E0B\u7EBF\u3002\u56E0\u6B64\uFF0Ckeepalive \u53EF\u4EE5\u5BDF\u89C9\u5F02\u5E38\u7684\u540C\u65F6\uFF0C\u4EA6\u53EF\u4EE5\u8FBE\u5230\u4F1A\u8BDD\u4FDD\u6301\u7684\u4F5C\u7528\u3002</p><p>\u6B64\u5916\uFF0C\u5728 CONNECT \u62A5\u6587\u91CC\u8BBE\u7F6E <code>CleanSession</code> \u5B57\u6BB5\u4E3A False \u7684\u8BBE\u5907\uFF0C\u65AD\u7EBF\u540E\u91CD\u65B0\u4E0A\u7EBF\u540E\uFF0C\u5219 broker \u4F1A\u5C06\u5B58\u50A8\u7684\uFF1A</p><ul><li>\u8BBE\u5907\u6240\u6709\u7684\u8BA2\u9605</li><li>\u8FD8\u672A\u88AB\u8BBE\u5907\u786E\u8BA4\u7684 QoS1 \u548C QoS \u6D88\u606F</li><li>\u8BBE\u5907\u79BB\u7EBF\u65F6\u9519\u8FC7\u7684\u6D88\u606F</li></ul><p>\u53D1\u9001\u7ED9\u5176\u3002</p><h2 id="\u9057\u5631\u673A\u5236" tabindex="-1">\u9057\u5631\u673A\u5236 <a class="header-anchor" href="#\u9057\u5631\u673A\u5236" aria-hidden="true">#</a></h2><p>MQTT \u8BBE\u8BA1\u4E86\u9057\u613F\uFF08Last Will\u3001Testament\uFF09 \u6D88\u606F\uFF0C\u8BA9 Broker \u5728\u53D1\u73B0\u8BBE\u5907\u5F02\u5E38\u4E0B\u7EBF\u7684\u60C5\u51B5\u4E0B\uFF0C\u5E2E\u52A9\u8BBE\u5907\u53D1\u5E03\u4E00\u6761\u9057\u613F\u6D88\u606F\u5230\u6307\u5B9A\u7684\u4E3B\u9898\u3002</p><p>\u5B9E\u9645\u4E0A\u5728\u67D0\u4E9B <a href="https://www.emqx.io/cn/products/broker" target="_blank" rel="noreferrer">MQTT \u670D\u52A1\u5668</a> \u7684\u5B9E\u73B0\u91CC (\u6BD4\u5982 EMQ X)\uFF0C\u8BBE\u5907\u4E0A\u7EBF\u6216\u4E0B\u7EBF\u7684\u65F6\u5019 broker \u4F1A\u901A\u8FC7\u67D0\u4E9B\u7CFB\u7EDF\u4E3B\u9898\u53D1\u5E03\u8BBE\u5907\u72B6\u6001\u66F4\u65B0\uFF0C\u66F4\u7B26\u5408\u5B9E\u9645\u5E94\u7528\u573A\u666F\u3002</p><h2 id="\u53C2\u8003" tabindex="-1">\u53C2\u8003 <a class="header-anchor" href="#\u53C2\u8003" aria-hidden="true">#</a></h2><ul><li><p><a href="https://juejin.im/post/6844904205463879688" target="_blank" rel="noreferrer">MQTT \u534F\u8BAE\u662F\u4EC0\u4E48</a></p></li><li><p><a href="https://www.runoob.com/w3cnote/mqtt-intro.html" target="_blank" rel="noreferrer">MQTT \u5165\u95E8\u4ECB\u7ECD</a></p></li></ul>',27),i=[a];function d(s,c,n,h,p,u){return l(),t("div",null,i)}const k=e(o,[["render",d]]);export{T as __pageData,k as default};
